import React from 'react';
import { Link } from 'react-router-dom';
import './Home.css'; // Import custom CSS for styling

const Home = () => {
  return (
    <div className="home-container">
      <div className="hero-section">
        <h1 className="hero-heading">Welcome to Our Blog</h1>
        <p className="hero-description">
          Explore insightful articles, share your thoughts, and connect with our community.
        </p>
        <Link to="/posts" className="btn-primary">View All Posts</Link>
      </div>
      <div className="featured-posts">
        <h2 className="section-heading">Featured Posts</h2>
        {/* Render featured posts here */}
        <div className="post-card">
          <h3 className="post-title">Post Title</h3>
          <p className="post-excerpt">Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
          <Link to="/posts/1" className="btn-secondary">Read More</Link>
        </div>
        {/* Repeat for other featured posts */}
      </div>
    </div>
  );
};

export default Home;
