import React from 'react';
import { useParams } from 'react-router-dom';
import './PostDetailPage.css'; // Import custom CSS for styling

const PostDetailPage = () => {
  const { postId } = useParams();

  // Fetch post details based on postId from your backend or wherever you store post data
  const post = {
    id: postId,
    title: 'Post Title',
    content: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris vel nulla sed nunc pharetra ultricies eget sit amet ex. Integer at malesuada nisi, et ullamcorper lacus. Pellentesque habitant morbi tristique senectus et netus et malesuada fames ac turpis egestas. Cras consequat consequat nunc sit amet efficitur. Sed non ante mauris. Duis nec nisl eros. Duis elementum est in erat feugiat, ut luctus libero efficitur. Sed viverra quam eget enim tristique, a ultrices justo faucibus. Nam ut libero vel nisi efficitur lacinia. Quisque maximus sit amet dolor eu porttitor. Donec convallis volutpat quam in convallis. Ut in ipsum velit. Integer euismod nisi id ligula venenatis, id lobortis libero tempor. Donec sed semper eros, nec fringilla lacus. Mauris id justo vitae justo volutpat maximus. Nam porttitor laoreet enim, vel laoreet sapien efficitur ac. Vestibulum a purus nec justo facilisis viverra sit amet at nulla.'
  };

  return (
    <div className="post-detail-container">
      <h1 className="post-detail-title">{post.title}</h1>
      <p className="post-detail-content">{post.content}</p>
    </div>
  );
};

export default PostDetailPage;
